package com.example.newspaper;

import androidx.appcompat.app.AppCompatActivity;
import androidx.recyclerview.widget.LinearLayoutManager;
import androidx.recyclerview.widget.RecyclerView;

import android.os.Bundle;
import android.util.Log;
import android.view.View;
import android.widget.ProgressBar;
import android.widget.Toast;

import java.util.ArrayList;

import retrofit2.Call;
import retrofit2.Callback;
import retrofit2.Response;
import retrofit2.Retrofit;
import retrofit2.converter.gson.GsonConverterFactory;

public class MainActivity extends AppCompatActivity implements CategoryRVAdapter.CategoryClickInterface {
    //api key
    //987e0da5eb854b58bcd296b7beb6dbee

    private RecyclerView newsrv,catrv;
    private ProgressBar pb;
    private ArrayList<Articles> articlesArrayList;
    private  ArrayList<CategoryRvModel> categoryRvModelArrayList;
    private CategoryRVAdapter categoryRVAdapter;
    private NewsRVAdapter newsRVAdapter;

    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_main);
        catrv =findViewById(R.id.rvcatagory);
        newsrv=findViewById(R.id.rvnews);
        pb=findViewById(R.id.pb);
        articlesArrayList=new ArrayList<>();
        categoryRvModelArrayList=new ArrayList<>();
        newsRVAdapter=new NewsRVAdapter(articlesArrayList,this);
        categoryRVAdapter=new CategoryRVAdapter(categoryRvModelArrayList,this,this:: onCategoryClick);
        newsrv.setLayoutManager(new LinearLayoutManager(this));
        newsrv.setAdapter(newsRVAdapter);
        catrv.setAdapter(categoryRVAdapter);
        getCat();
        getNews("All");
        newsRVAdapter.notifyDataSetChanged();
    }
    private void getCat(){
        categoryRvModelArrayList.add(new CategoryRvModel("All","data:image/jpeg;base64,"));
        categoryRvModelArrayList.add(new CategoryRvModel("Technology","https://img.lovepik.com//photo/50007/7678.jpg_300.jpg"));
        categoryRvModelArrayList.add(new CategoryRvModel("Science","https://i.pinimg.com/736x/c4/ed/23/c4ed2397c0f68076e613cead6a737f16.jpg"));
        categoryRvModelArrayList.add(new CategoryRvModel("Sports","https://thumbs.dreamstime.com/z/color-sport-background-football-soccer-basketball-hockey-box-tennis-baseball-vector-illustration-colorful-people-silhouettes-color-139030083.jpg"));
        categoryRvModelArrayList.add(new CategoryRvModel("General","data:image/jpeg;base64,"));
        categoryRvModelArrayList.add(new CategoryRvModel("Business","https://img.freepik.com/free-photo/business-partners-handshake-global-corporate-with-technology-concept_53876-102615.jpg?w=2000"));
        categoryRvModelArrayList.add(new CategoryRvModel("Health","https://c8.alamy.com/comp/T84XXF/seamless-pattern-with-healthcare-medicine-and-pharmacy-icons-and-symbols-medical-background-doodle-T84XXF.jpg"));

        categoryRVAdapter.notifyDataSetChanged();
    }
    private void getNews(String category){
        pb.setVisibility(View.VISIBLE);
        articlesArrayList.clear();
        String caturl="https://newsapi.org/v2/top-headlines/sources?apiKey=987e0da5eb854b58bcd296b7beb6dbee&category="+category;
        String url="https://newsapi.org/v2/top-headlines?language=en&excludeDomains=stackoverflow.com&sortBy=publishedAt&apiKey=987e0da5eb854b58bcd296b7beb6dbee";
        String base_url="https://newsapi.org/";
        Retrofit retrofit =new Retrofit.Builder()
                .baseUrl(base_url)
                .addConverterFactory(GsonConverterFactory.create())
                .build();
        retrofit_api retrofit_api= retrofit.create(retrofit_api.class);
        Call<NewsModel> call;
        if(category.equals("All")){
            call=retrofit_api.getAllNews(url);
        }
        else{
            call=retrofit_api.getNewsbyCategory(caturl);
        }
        call.enqueue(new Callback<NewsModel>() {
            @Override
            public void onResponse(Call<NewsModel> call, Response<NewsModel> response) {
                NewsModel newsModel=response.body();
                pb.setVisibility(View.GONE);
                ArrayList<Articles> articles=newsModel.getArticles();

                if(articles!=null){
                    for(int i=0;i<articles.size();i++){
                        articlesArrayList.add(new Articles(articles.get(i).getTitle(),articles.get(i).getDescription(),articles.get(i).getUrltoImage(),
                                articles.get(i).getUrl(),articles.get(i).getContent()));

                    }
                }
                else Log.e("ar","null");


                newsRVAdapter.notifyDataSetChanged();
            }

            @Override
            public void onFailure(Call<NewsModel> call, Throwable t) {
                Toast.makeText(MainActivity.this, "failed to get news", Toast.LENGTH_SHORT).show();

            }
        });

    }

    @Override
    public void onCategoryClick(int position) {
        String cat=categoryRvModelArrayList.get(position).getCategory();
        getNews(cat);

    }
}